import React from 'react';
import Header from '../components/Header';
import Loading from '../components/Loading';

class ProfileEdit extends React.Component {
  constructor() {
    super();

    this.state = {
      Name: '',
      Email: '',
      Description: '',
      Imagem: '',
      isLoading: false,
      ButtonDisable: true,
    };
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const response = await getUser();
    this.setState({
      Name: response.name,
      Email: response.email,
      Description: response.description,
      Imagem: response.image,
      isLoading: false,
      ButtonDisable: false });
  }

  handleChange = ({ target }) => {
    const stateName = target.name;
    const { value } = target;

    this.setState({
      [stateName]: value,
    }, () => this.validateForm(this.state));
  };

  render() {
    const {
      inputDescription,
      inputEmail,
      inputImageUrl,
      inputName,
      isLoading,
      isSaveProfileButtonDisabled,
    } = this.state;
    return (
      <section>
        {isLoading ? <Loading /> : (
          <div data-testid="page-profile-edit">
            <Header />
            <form>
              <label>
                Nome:
                <input
                  name="inputName"
                  id="profile-edit-input-name"
                  type="text"
                  value={ inputName }
                  onChange={ this.handleChange }
                  data-testid="edit-input-name"
                />
              </label>
              <label>
                Email:
                <input
                  name="inputEmail"
                  id="profile-edit-input-email"
                  type="text"
                  value={ inputEmail }
                  onChange={ this.handleChange }
                  data-testid="edit-input-email"
                />
              </label>
              <label>
                Descrição:
                <input
                  name="inputDescription"
                  id="profile-edit-input-description"
                  type="text"
                  value={ inputDescription }
                  onChange={ this.handleChange }
                  data-testid="edit-input-description"
                />
              </label>
              <label>
                Foto:
                <input
                  name="inputImageUrl"
                  id="profile-edit-input-image"
                  type="text"
                  value={ inputImageUrl }
                  onChange={ this.handleChange }
                  data-testid="edit-input-image"
                />
              </label>
              <button
                disabled={ isSaveProfileButtonDisabled }
                data-testid="edit-button-save"
                type="submit"
              >
                Salvar

              </button>
            </form>
          </div>)}
      </section>
    );
  }
}

export default ProfileEdit;
