import React from 'react';
import Header from '../components/Header';
import { getUser, updateUser } from '../services/userAPI';
import Loading from '../components/Loading';

class ProfileEdit extends React.Component {
  constructor() {
    super();

    this.state = {
      inputName: '',
      inputEmail: '',
      inputDescription: '',
      inputImageUrl: '',
      isLoading: false,
      isSaveProfileButtonDisabled: true,
    };
  }

  componentDidMount() {
    this.loadUser();
  }

  handleChange = ({ target }) => {
    const stateName = target.name;
    const { value } = target;

    this.setState({
      [stateName]: value,
    }, () => this.validateForm(this.state));
  };

  validateForm = (data) => {
    const {
      inputDescription,
      inputEmail,
      inputImageUrl,
      inputName,
    } = data;

    const emailRegex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g;

    const inputDescriptionIsFilled = !!inputDescription;
    const inputEmailIsAValidEmail = emailRegex.test(inputEmail);
    const inputImageUrlIsFilled = !!inputImageUrl;
    const inputNameIsFilled = !!inputName;

    const validations = [
      inputDescriptionIsFilled,
      inputEmailIsAValidEmail,
      inputImageUrlIsFilled,
      inputNameIsFilled,
    ];

    const formIsValid = validations.every((validation) => validation === true);

    this.setState({
      isSaveProfileButtonDisabled: !formIsValid,
    });
  };

  loadUser = () => {
    this.setState({
      isLoading: false,
    }, async () => {
      const user = await getUser();

      this.setState({
        inputName: user.name,
        inputEmail: user.email,
        inputDescription: user.description,
        inputImageUrl: user.image,
        isLoading: false,
        isSaveProfileButtonDisabled: false,
      });
    });
  };

  handleSubmit = async (event) => {
    event.preventDefault();

    const {
      inputDescription,
      inputEmail,
      inputImageUrl,
      inputName,
    } = this.state;

    const data = {
      description: inputDescription,
      email: inputEmail,
      image: inputImageUrl,
      name: inputName,
    };

    updateUser(data);

    const { history } = this.props;
    history.push('/profile');
  };

  render() {
    const {
      inputDescription,
      inputEmail,
      inputImageUrl,
      inputName,
      isLoading,
      isSaveProfileButtonDisabled,
    } = this.state;

    return (
      <div data-testid="page-profile-edit">
        {isLoading ? <Loading /> : (
          <form onSubmit={ this.handleSubmit }>
            <label>
              Nome:
              <input
                name="inputName"
                id="profile-edit-input-name"
                type="text"
                value={ inputName }
                onChange={ this.handleChange }
                data-testid="edit-input-name"
              />
            </label>
            <label>
              Email:
              <input
                name="inputEmail"
                id="profile-edit-input-email"
                type="text"
                value={ inputEmail }
                onChange={ this.handleChange }
                data-testid="edit-input-email"
              />
            </label>
            <label>
              Descrição:
              <input
                name="inputDescription"
                id="profile-edit-input-description"
                type="text"
                value={ inputDescription }
                onChange={ this.handleChange }
                data-testid="edit-input-description"
              />
            </label>
            <label>
              Imagem Url:
              <input
                name="inputImageUrl"
                id="profile-edit-input-image"
                type="text"
                value={ inputImageUrl }
                onChange={ this.handleChange }
                data-testid="edit-input-image"
              />
            </label>
            <button
              disabled={ isSaveProfileButtonDisabled }
              data-testid="edit-button-save"
              type="submit"
            >
              Salvar
            </button>
          </form>
        )}
      </div>
    );
  }
}

export default ProfileEdit;
