import React from 'react';
import Header from '../components/Header';
import Loading from '../components/Loading';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      InputName: '',
      DisableButton: true,
      isLoading: true,
      album: false,
      InputNameSearched: '',
      albumName: [],
    };
  }

  handleChange = ({ target }) => {
    const min = 2;
    this.setState({ [target.name]: target.value });
    if (target.value.length >= min) {
      this.setState({ DisableButton: false });
    } else {
      this.setState({ DisableButton: true });
    }
  };

  SearchAlbum = async () => {
    const { InputName } = this.state;
    this.setState({ isLoading: false, InputNameSearched: InputName });
    const namemusic = InputName;
    const response = await searchAlbumsAPI(namemusic);
    console.log(response);
    response.map((album) => this.setState((prevState) => {
      [prevState, album];
    }));
    this.setState({ InputName: '', isLoading: true, album: true });
  };

  render() {
    const { InputName, DisableButton, isLoading, album,
      InputNameSearched, albumName } = this.state;
    console.log(albumName);
    return (
      <section>
        <Header />
        <p data-testid="page-search" />
        {
          !isLoading
            ? <Loading /> : (
              <label>
                <input
                  data-testid="search-artist-input"
                  type="text"
                  name="InputName"
                  value={ InputName }
                  onChange={ this.handleChange }
                />
                <button
                  type="button"
                  data-testid="search-artist-button"
                  disabled={ DisableButton }
                  onClick={ this.SearchAlbum }
                >
                  Pesquisar

                </button>
              </label>
            )
        }
        {
          album && (
            <p>{`Resultado de álbuns de: ${InputNameSearched}`}</p>)
        }
      </section>
    );
  }
}

export default Search;
