import React from 'react';
import Header from '../components/Header';

class ProfileEdit extends React.Component {
  constructor() {
    super();

    this.state = {
      Name: '',
      Email: '',
      Description: '',
      Imagem: '',
      isLoading: false,
      ButtonDisable: true,
    };
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const response = await getUser();
    this.setState({
      Name: response.name,
      Email: response.email,
      Description: response.description,
      Imagem: response.image,
      isLoading: false,
      ButtonDisable: false });
  }

  render() {
    const { information, isLoading } = this.state;
    return (
      <section>
        <div data-testid="page-profile-edit">
          <Header />
          <form>
            <label>
              Nome:
              <input
                name="inputName"
                id="profile-edit-input-name"
                type="text"
                value={ inputName }
                onChange={ this.handleChange }
                data-testid="edit-input-name"
              />
            </label>
            <label>
              Email:
              <input data-testid="edit-input-email" />
            </label>
            <label>
              Descrição:
              <input data-testid="edit-input-description" />
            </label>
            <label>
              Foto:
              <input data-testid="edit-input-image" />
            </label>
            <button data-testid="edit-button-save">Salvar</button>
          </form>
        </div>
      </section>
    );
  }
}

export default ProfileEdit;
