import React from 'react';
import { Redirect } from 'react-router-dom';
// import PropyTypes from 'prop-types';
import Header from '../components/Header';
import Loading from '../components/Loading';
import { getUser, updateUser } from '../services/userAPI';

class ProfileEdit extends React.Component {
  constructor() {
    super();

    this.state = {
      Name: '',
      Email: '',
      Description: '',
      Imagem: '',
      isLoading: false,
      ButtonDisable: true,
    };
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const response = await getUser();
    this.setState({
      Name: response.name,
      Email: response.email,
      Description: response.description,
      Imagem: response.image,
      isLoading: false,
      ButtonDisable: false });
  }

  handleChange = ({ target }) => {
    const stateName = target.name;
    const { value } = target;

    this.setState({
      [stateName]: value,
    }, () => this.FormValidate(this.state));
  };

  FormValidate = (response) => {
    const { Description, Email, Imagem, Name } = response;

    const emailRegex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g;

    const DescriptionIsFilled = !!Description;
    const EmailIsAValidEmail = emailRegex.test(Email);
    const ImageUrlIsFilled = !!Imagem;
    const NameIsFilled = !!Name;

    const validations = [DescriptionIsFilled,
      EmailIsAValidEmail, ImageUrlIsFilled, NameIsFilled];

    const formIsValid = validations.every((validation) => validation === true);

    this.setState({ ButtonDisable: !formIsValid });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { Description, Email, Imagem, Name } = this.state;
    const response = { description: Description,
      email: Email,
      image: Imagem,
      name: Name };
    updateUser(response);
    this.Redirect();
  };

  Redirect = () => {
    <Redirect to="/profile" />;
  };

  render() {
    const {
      Description,
      Email,
      Imagem,
      Name,
      isLoading,
      ButtonDisable,
    } = this.state;
    return (
      <section>
        <Header />
        {isLoading ? <Loading /> : (
          <div data-testid="page-profile-edit">
            <form>
              <label>
                Nome:
                <input
                  name="Name"
                  id="profile-edit-input-name"
                  type="text"
                  value={ Name }
                  onChange={ this.handleChange }
                  data-testid="edit-input-name"
                />
              </label>
              <label>
                Email:
                <input
                  name="Email"
                  id="profile-edit-input-email"
                  type="text"
                  value={ Email }
                  onChange={ this.handleChange }
                  data-testid="edit-input-email"
                />
              </label>
              <label>
                Descrição:
                <textarea
                  name="Description"
                  id="profile-edit-input-description"
                  type="text"
                  value={ Description }
                  onChange={ this.handleChange }
                  data-testid="edit-input-description"
                />
              </label>
              <label>
                Foto:
                <input
                  name="ImageUrl"
                  id="profile-edit-input-image"
                  type="text"
                  value={ Imagem }
                  onChange={ this.handleChange }
                  data-testid="edit-input-image"
                />
              </label>
              <button
                disabled={ ButtonDisable }
                data-testid="edit-button-save"
                type="submit"
                onClick={ this.handleSubmit }
              >
                Salvar

              </button>
            </form>
          </div>)}
      </section>
    );
  }
}

export default ProfileEdit;
