                <code>audio</code>
                .
              </audio>
              <label className='AudioContent' data-testid={ `checkbox-music-${trackId}` }>
                Favorita
                <input
                  className='coracao'
                  type="checkbox"
                  name="isfavorite"
                  checked={ isfavorite }
                  onChange={ this.FavoriteSong }
                />
              </label>
            </section>)
        }
      </section>
    );
  }
}

MusicCard.propTypes = {
  previewUrl: PropTypes.string.isRequired,
  trackName: PropTypes.string.isRequired,
  trackId: PropTypes.number.isRequired,
  Remove: PropTypes.func.isRequired,
};

export default MusicCard;
