import React from 'react';
import Header from '../components/Header';
import { getUser, updateUser } from '../services/userAPI';

class ProfileEdit extends React.Component {
  constructor() {
    super();

    this.state = {
      inputName: '',
      inputEmail: '',
      inputDescription: '',
      inputImageUrl: '',
      isLoading: false,
      isSaveProfileButtonDisabled: true,
    };
  }

  componentDidMount() {
    this.loadUser();
  }

  handleChange = ({ target }) => {
    const stateName = target.name;
    const { value } = target;

    this.setState({
      [stateName]: value,
    }, () => this.validateForm(this.state));
  };

  validateForm = (data) => {
    const {
      inputDescription,
      inputEmail,
      inputImageUrl,
      inputName,
    } = data;

    const emailRegex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g;

    const inputDescriptionIsFilled = !!inputDescription;
    const inputEmailIsAValidEmail = emailRegex.test(inputEmail);
    const inputImageUrlIsFilled = !!inputImageUrl;
    const inputNameIsFilled = !!inputName;

    const validations = [
      inputDescriptionIsFilled,
      inputEmailIsAValidEmail,
      inputImageUrlIsFilled,
      inputNameIsFilled,
    ];

    const formIsValid = validations.every((validation) => validation === true);

    this.setState({
      isSaveProfileButtonDisabled: !formIsValid,
    });
  };

  loadUser = () => {
    this.setState({
      isLoading: false,
    }, async () => {
      const user = await getUser();

      this.setState({
        inputName: user.name,
        inputEmail: user.email,
        inputDescription: user.description,
        inputImageUrl: user.image,
        isLoading: false,
        isSaveProfileButtonDisabled: false,
      });
    });
  };

  handleSubmit = async (event) => {
    event.preventDefault();

    const {
      inputDescription,
      inputEmail,
      inputImageUrl,
      inputName,
    } = this.state;

    const data = {
      description: inputDescription,
      email: inputEmail,
      image: inputImageUrl,
      name: inputName,
    };

    updateUser(data);

    const { history } = this.props;
    history.push('/profile');
  };

  render() {
    const { information, isLoading } = this.state;
    const { name, email, imagem, description } = information;
    return (
      <section>
        <div data-testid="page-profile-edit">
          <Header />
          <form>
            <label>
              Nome:
              <input data-testid="edit-input-name" />
            </label>
            <label>
              Email:
              <input data-testid="edit-input-email" />
            </label>
            <label>
              Descrição:
              <input data-testid="edit-input-description" />
            </label>
            <label>
              Foto:
              <input data-testid="edit-input-image" />
            </label>
            <button data-testid="edit-button-save">Salvar</button>
          </form>
        </div>
      </section>
    );
  }
}

export default ProfileEdit;
