import React from 'react';
import Header from '../components/Header';
import Loading from '../components/Loading';

class Profile extends React.Component {
  constructor() {
    super();

    this.state = {
      name: '',
      email: 'usuario@usuario.com.br',
      descricao: 'Lorem ipsium',
      img: 'https://mundoconectado.com.br/uploads/chamadas/capa_145.jpg',
      isLoading: false,

    };
  }

  getUserPromisse = async () => {
    this.setState({ isLoading: true });
    const response = await getUser();
    const data = await response;
    const { name } = data;
    this.setState({ name, isLoading: false });
  };

  render() {
    this.getUserPromisse();
    const { name, email, descricao, img, isLoading } = this.state;
    return (
      <section>
        <Header />
        <p data-testid="page-profile" />
        <h3>{name}</h3>
        <p>{email}</p>
        <span>{descricao}</span>
        <img data-testid="profile-image" src={ img } alt="Pessoa" />
        {
          isLoading && <Loading />
        }
      </section>
    );
  }
}

export default Profile;
